mapper.select:
SELECT aa.point_id, aa.point_name, aa.unit_id,
MAX ( CASE WHEN data_time = #{item.dateTime} THEN "value" END ) AS ${item.value},
aa.time_type FROM ( SELECT ni.point_id, ei.NAME AS "point_name", ei.unit_id, di."value", di.data_time, di.time_type FROM model_node mn LEFT JOIN node_point ni ON ni.node_id = mn.node_id LEFT JOIN meter_point ei ON ei.point_id = ni.point_id LEFT JOIN energy_used di ON ni.point_id = di.point_id WHERE di.data_time >= #{beginTime} AND di.data_time < #{endTime} AND di.point_id IN
#{indexId}
AND di.time_type = #{timeType}
and ei.energy_id=#{indexStorageId}
) aa GROUP BY aa.point_id, aa.point_name, aa.unit_id, aa.time_type

SELECT di.point_id, ci.name AS point_name, ci.unit_id, di."value", di.data_time, di.time_type, di.time_code FROM energy_used di LEFT JOIN meter_point ci ON di.point_id = ci.point_id WHERE di.point_id =#{indexId} AND di.data_time < #{endTime}
AND di.data_time >= #{beginTime}
AND di.time_type = #{timeType}
order by di.data_time
